CREATE VIEW dbo.SQL_Size_Control
AS
SELECT  TOP (100) PERCENT t.name AS TableName, s.name AS SchemaName, FORMAT(p.rows, 'N0') AS rows, 
               CAST(ROUND(SUM(a.total_pages) * 8 / 1024.00 / 1024.00, 2) AS NUMERIC(36, 0)) AS TotalSpaceGB
FROM     sys.tables AS t INNER JOIN
               sys.indexes AS i ON t.object_id = i.object_id INNER JOIN
               sys.partitions AS p ON i.object_id = p.object_id AND i.index_id = p.index_id INNER JOIN
               sys.allocation_units AS a ON p.partition_id = a.container_id LEFT OUTER JOIN
               sys.schemas AS s ON t.schema_id = s.schema_id
WHERE  (t.name NOT LIKE 'dt%') AND (t.is_ms_shipped = 0) AND (i.object_id > 255)
GROUP BY t.name, s.name, p.rows
ORDER BY TotalSpaceGB DESC, TableName
