CREATE PROCEDURE [dbo].[GPC_UpdateZgKoef]
    @dryRun BIT = 0
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @resultMsg NVARCHAR(500)
DECLARE @setTo1 INT = 0,
        @setTo2 INT = 0,
        @setTo3 INT = 0,
        @setTo4 INT = 0,
        @setTo5 INT = 0

    -- ავტომატურად ვადგენ წინა კვარტალს
    DECLARE @now DATE = GETDATE() --'2024-04-05'
    DECLARE @year INT = YEAR(@now)
    DECLARE @month INT = MONTH(@now)

    DECLARE @qStart DATE
    DECLARE @qEnd DATE

    IF @month BETWEEN 1 AND 3
    BEGIN
        SET @qStart = DATEFROMPARTS(@year - 1, 10, 1)
        SET @qEnd = DATEFROMPARTS(@year - 1, 12, 31)
    END
    ELSE IF @month BETWEEN 4 AND 6
    BEGIN
        SET @qStart = DATEFROMPARTS(@year, 1, 1)
        SET @qEnd = DATEFROMPARTS(@year, 3, 31)
    END
    ELSE IF @month BETWEEN 7 AND 9
    BEGIN
        SET @qStart = DATEFROMPARTS(@year, 4, 1)
        SET @qEnd = DATEFROMPARTS(@year, 6, 30)
    END
    ELSE
    BEGIN
        SET @qStart = DATEFROMPARTS(@year, 7, 1)
        SET @qEnd = DATEFROMPARTS(@year, 9, 30)
    END

    IF OBJECT_ID('tempdb..#tmp_calc') IS NOT NULL DROP TABLE #tmp_calc

    SELECT 
        pnn,
        CASE 
            WHEN SUM(tanx) < 100 THEN 1
            WHEN SUM(tanx) >= 100 AND SUM(tanx) < 300 THEN 2
            WHEN SUM(tanx) >= 300 AND SUM(tanx) < 450 THEN 3
            WHEN SUM(tanx) >= 450 AND SUM(tanx) < 850 THEN 4
            WHEN SUM(tanx) >= 850 THEN 5
        END AS new_koef
    INTO #tmp_calc
    FROM crm_zgarbi_qulebi
    WHERE tar BETWEEN @qStart AND @qEnd AND qula > 0
    GROUP BY pnn

    -- koef = 1
    IF @dryRun = 0
    BEGIN
        UPDATE p
        SET zg_koef_test = 1
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 1 AND ISNULL(p.zg_koef_test, 0) <> 1 AND ISNULL(p.zg_koef_test, 0)  > 0.99
        SET @setTo1 = @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo1 = COUNT(*)
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 1 AND ISNULL(p.zg_koef_test, 0) <> 1 AND ISNULL(p.zg_koef_test, 0)  > 0.99
    END

    -- koef = 2
    IF @dryRun = 0
    BEGIN
        UPDATE p
        SET zg_koef_test = 2
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 2 AND ISNULL(p.zg_koef_test, 0) <> 2 AND ISNULL(p.zg_koef_test, 0)  > 0.99
        SET @setTo2 = @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo2 = COUNT(*)
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 2 AND ISNULL(p.zg_koef_test, 0) <> 2 AND ISNULL(p.zg_koef_test, 0)  > 0.99
    END

    -- koef = 3
    IF @dryRun = 0
    BEGIN
        UPDATE p
        SET zg_koef_test = 3
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 3 AND ISNULL(p.zg_koef_test, 0) <> 3 AND ISNULL(p.zg_koef_test, 0)  > 0.99
        SET @setTo3 = @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo3 = COUNT(*)
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 3 AND ISNULL(p.zg_koef_test, 0) <> 3 AND ISNULL(p.zg_koef_test, 0)  > 0.99
    END

    -- koef = 4
    IF @dryRun = 0
    BEGIN
        UPDATE p
        SET zg_koef_test = 4
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 4 AND ISNULL(p.zg_koef_test, 0) <> 4 AND ISNULL(p.zg_koef_test, 0)  > 0.99
        SET @setTo4 = @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo4 = COUNT(*)
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 4 AND ISNULL(p.zg_koef_test, 0) <> 4 AND ISNULL(p.zg_koef_test, 0)  > 0.99
    END

    -- koef = 5
    IF @dryRun = 0
    BEGIN
        UPDATE p
        SET zg_koef_test = 5
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 5 AND ISNULL(p.zg_koef_test, 0) <> 5 AND ISNULL(p.zg_koef_test, 0)  > 0.99
        SET @setTo5 = @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo5 = COUNT(*)
        FROM er_pac p
        INNER JOIN #tmp_calc c ON p.pn = c.pnn
        WHERE c.new_koef = 5 AND ISNULL(p.zg_koef_test, 0) <> 5 AND ISNULL(p.zg_koef_test, 0)  > 0.99
    END

    -- koef = 1 მათთვის, ვისაც საერთოდ არ აქვს აქტივობა
    IF @dryRun = 0
    BEGIN
        UPDATE er_pac
        SET zg_koef_test = 1
        WHERE pn NOT IN (SELECT pnn FROM #tmp_calc)
          AND ISNULL(zg_koef_test, 0) <> 1
        SET @setTo1 = @setTo1 + @@ROWCOUNT
    END
    ELSE
    BEGIN
        SELECT @setTo1 = @setTo1 + COUNT(*)
        FROM er_pac
        WHERE pn NOT IN (SELECT pnn FROM #tmp_calc) AND ISNULL(zg_koef_test, 0) <> 1 AND ISNULL(zg_koef_test, 0)  > 0.99
    END

    -- შედეგის ტექსტი
SET @resultMsg = 
    N'⭐1: ' + ISNULL(NULLIF(FORMAT(@setTo1, '#,###'), ''), '0') +
    N', ⭐2: ' + ISNULL(NULLIF(FORMAT(@setTo2, '#,###'), ''), '0') +
    N', ⭐3: ' + ISNULL(NULLIF(FORMAT(@setTo3, '#,###'), ''), '0') +
    N', ⭐4: ' + ISNULL(NULLIF(FORMAT(@setTo4, '#,###'), ''), '0') +
    N', ⭐5: ' + ISNULL(NULLIF(FORMAT(@setTo5, '#,###'), ''), '0')

    SELECT @resultMsg AS შედეგი
END
GO
