CREATE PROCEDURE [dbo].[macivari_analizi]
		(@k int)
AS
	SELECT  nass.na_kod, CASE WHEN min_adg IS NULL THEN '' ELSE min_adg END AS min_adg, 
	               CASE WHEN na IS NULL THEN 0 ELSE na END AS na, '' AS med_geo
	FROM     (SELECT  SUM(na_raod) AS na, na_kod, na_saw
	                FROM     nasti
	                WHERE  (na_saw = @k)
	                GROUP BY na_kod, na_saw) AS nass LEFT OUTER JOIN
	                   (SELECT DISTINCT min_med, min_adg
	                    FROM     minim
	                    WHERE  (min_saw = @k)) AS adg ON nass.na_kod = adg.min_med
	RETURN
GO
