CREATE PROCEDURE [dbo].[ModifyReplicatedTable]
(@act    VARCHAR(4), 
 @tbname VARCHAR(50), 
 @clname VARCHAR(50), 
 @dtype  VARCHAR(50) = NULL, 
 @null   BIT         = 1
)
WITH EXECUTE AS 'dbo'
AS
     BEGIN
         DECLARE @sql VARCHAR(4000);
         IF @act = 'add'
            AND @dtype IS NOT NULL
             BEGIN
                 SET @sql = 'ALTER TABLE '+@tbname+' ADD '+@clname+' '+@dtype+CASE
                                                                                  WHEN @null = 0
                                                                                  THEN ' '+'NOT NULL CONSTRAINT DF_'+@tbname+'_'+@clname+' DEFAULT '''''
                                                                                  ELSE ''
                                                                              END;
                 EXEC (@sql);
             END;
         IF @act = 'edit'
            AND @dtype IS NOT NULL
             BEGIN
                 SET @sql = 'ALTER TABLE '+@tbname+' ALTER COLUMN '+@clname+' '+@dtype;
                 EXEC (@sql);
             END;
         IF @act = 'drop'
             BEGIN
                 IF EXISTS
                 (
                     SELECT name
                     FROM sys.objects
                     WHERE name = 'DF_'+@tbname+'_'+@clname
                 )
                     BEGIN
                         SET @sql = 'ALTER TABLE '+@tbname+' DROP CONSTRAINT DF_'+@tbname+'_'+@clname;
                         EXEC (@sql);
                     END;
                 SET @sql = 'ALTER TABLE '+@tbname+' DROP COLUMN '+@clname;
                 EXEC (@sql);
             END;
     END;
GO
