CREATE PROCEDURE dbo.operac_mimd_monac

	(
	@tipi nvarchar(2),
	@k int
	)

AS
	if @tipi = '2'
	BEGIN
		SELECT  nasti.na_kod, nasti.na_raod, gasv.gasv_k, semos.semos_seria, semos.semos_vada, gasv.gasv_fasi, 
					   gasv.gasv_vat, gasv.gasv_fasi_ziritadi, gasv.gasv_sen
		FROM     semos  RIGHT OUTER JOIN
					   nasti ON semos.semos_k = nasti.na_k LEFT OUTER JOIN
					   gasv ON nasti.na_kavs = gasv.gasv_k
		WHERE  (nasti.na_tipi = 4) AND (gasv.gasv_gas_k = @k) 
		RETURN
	END
		

	if @tipi = '1'
	BEGIN
		SELECT  nasti.na_kod, nasti.na_raod, semos.semos_k, semos.semos_seria, semos.semos_vada, 
		               semos.semos_fasi, semos.semos_vat, semos.semos_real_fasi
		FROM     semos RIGHT OUTER JOIN
		               nasti ON semos.semos_k = nasti.na_kavs
		WHERE  (nasti.na_tipi = 1) AND (semos.semos_sem_k = @k)
		RETURN
	END
	
	
		if @tipi = '3'
	BEGIN
		SELECT  nasti.na_kod, nasti.na_raod, sb1.br_k, semos.semos_seria, semos.semos_vada, sb1.br_sen
		FROM     nasti LEFT OUTER JOIN
		               semos ON nasti.na_k = semos.semos_k LEFT OUTER JOIN
		               sb1 ON nasti.na_kavs = sb1.br_k
		WHERE  (nasti.na_tipi = 3) AND (sb1.br_nn = @k)
		RETURN
	END
	
	
	RETURN
