CREATE PROCEDURE [dbo].[yvela_med_nasti0]
	(@k int )
AS
      SELECT sawy.sawy_k, sawy.sawy_das, sawy.sawy_misamarti, sawy.sawy_tel, sawy.sawy_mail, 
                     semos.semos_seria, semos.semos_vada, SUM(v.rao) AS na, 
                     SUM((semos.semos_fasi * sem.sem_kursi * v.rao) * (semos.semos_vat + sem.sem_pr / 100)) AS usd, 
                     SUM((semos.semos_fasi * sem.sem_kursi * sem.sem_kursi_lari * v.rao) 
                     * (semos.semos_vat + sem.sem_pr / 100)) AS lar, 
                     SUM((semos.semos_fasi * sem.sem_kursi * sem.sem_kursi_evro * v.rao) 
                     * (semos.semos_vat + sem.sem_pr / 100)) AS evr
      FROM     (SELECT  SUM(na_raod) AS rao, na_k, na_saw
                      FROM     nasti
                      WHERE  (na_kod = @k)
                      GROUP BY na_k, na_saw) AS v INNER JOIN
                     semos ON v.na_k = semos.semos_k INNER JOIN
                     sem ON semos.semos_sem_k = sem.sem_k INNER JOIN
                     sawy ON v.na_saw = sawy.sawy_k
      GROUP BY sawy.sawy_k, sawy.sawy_das, sawy.sawy_misamarti, sawy.sawy_tel, sawy.sawy_mail, 
                     semos.semos_seria, semos.semos_vada
      HAVING  (SUM(v.rao) > 0)
      ORDER BY sawy.sawy_k
      
	RETURN
GO
